mapper.select:
SELECT COUNT(*)
FROM REPORTS


		WHERE MEMBER_ID = #{keyword}


		WHERE TARGET_MEMBER_ID = #{keyword}


		WHERE DETAIL LIKE '%' || #{keyword} || '%'

	SELECT COUNT(*)
	FROM REPORTS


            AND TYPE = #{type}


            AND STATUS = #{status}

SELECT REPORTS_ID ,MEMBER_ID ,TARGET_MEMBER_ID ,POST_ID ,REPLY_ID ,REVIEW_ID ,TYPE ,REASON ,STATUS ,CREATED_AT ,RESOLVED_AT FROM REPORTS
WHERE MEMBER_ID = #{keyword}
WHERE TARGET_MEMBER_ID = #{keyword}
WHERE DETAIL LIKE '%' || #{keyword} || '%'
ORDER BY CREATED_AT DESC

SELECT REPORTS_ID ,MEMBER_ID ,TARGET_MEMBER_ID ,POST_ID ,REPLY_ID ,REVIEW_ID ,TYPE ,REASON ,STATUS ,CREATED_AT ,RESOLVED_AT FROM REPORTS
AND TYPE = #{type}
AND STATUS = #{status}
ORDER BY CREATED_AT DESC

mapper.insert:
	INSERT INTO REPORTS (
		REPORTS_ID
		,MEMBER_ID
		,TARGET_MEMBER_ID
		,TYPE
		,POST_ID
		,REPLY_ID
		,REVIEW_ID
		,REASON
		,DETAIL
	) VALUES (
		REPORTS_ID_SEQ.NEXTVAL
		,#{memberId}
		,#{targetMemberId}
		,#{type}
		,
            NULL
            #{postId}
        ,

            NULL
            #{replyId}
        ,

            NULL
            #{reviewId}

		,#{reason}
		,#{detail}
	)

mapper.update:
UPDATE REPORTS
SET REASON = #{reason}
	,DETAIL = #{detail}
WHERE REPORTS_ID = #{reportsId}
AND MEMBER_ID = #{memberId}
AND STATUS IS NULL

mapper.delete:
DELETE FROM REPORTS
WHERE REPORTS_ID = #{reportsId}
AND MEMBER_ID = #{memberId}
AND STATUS IS NULL

mapper.select:
		SELECT COUNT(*)
		FROM REPORTS
	    WHERE STATUS = 'RESOLVED'
        AND TYPE = #{type}


	            AND POST_ID = #{postId}


	            AND REPLY_ID = #{replyId}


	            AND REVIEW_ID = #{reviewId}
